    attribute_value_type="raw",
    host=~"(omv|synology.*)",
    attribute_name!~"^(Temperature_Celsius|Spin_Up_Time|Load_Cycle_Count|Power_On_Hours|Power-Off_Retract_Count|Power_Cycle_Count|Throughput_Performance|Seek_Time_Performance|Helium_Level|Start_Stop_Count)$"
}) by (host, device)

smartctl_device_attribute{
    attribute_name="Wear_Leveling_Count",
    attribute_value_type="value"
}

label_join (
    100 * (
        node_filesystem_size_bytes{
            job="node-exporter",
            host=~"^(k8s.*|proxmox.*)$",
            fstype!~"^(nfs|tmpfs|vfat|fuse).*$",
            mountpoint=~"^(/|/volume1|/srv/.*)$"
        } - node_filesystem_avail_bytes{
            job="node-exporter",
            host=~"^(k8s.*|proxmox.*)$",
            fstype!~"^(nfs|tmpfs|vfat|fuse).*$",
            mountpoint=~"^(/|/volume1|/srv/.*)$"
        }
    ) / node_filesystem_size_bytes{
        job="node-exporter",
        host=~"^(k8s.*|proxmox.*)$",
        fstype!~"^(nfs|tmpfs|vfat|fuse).*$",
        mountpoint=~"^(/|/volume1|/srv/.*)$"
    },
    "host_and_mount", "@", "host", "mountpoint"
)

label_join (
    100 * (
        node_filesystem_size_bytes{
            job="node-exporter",
            host!~"^(k8s.*|proxmox.*)$",
            fstype!~"^(nfs|tmpfs|vfat|fuse).*$",
