"host_and_mount", "@", "host", "mountpoint")

label_join (
    100 * (windows_logical_disk_size_bytes{
        volume="C:"
    } - windows_logical_disk_free_bytes{
        volume="C:"
    }) / windows_logical_disk_size_bytes{
        volume="C:"
    },
"host_and_mount", "@", "host", "mountpoint")

